import React, { Component } from "react";
import Navbar from "./navbar";
const pd = require("paralleldots");

pd.apiKey = "M0crWrIdDTIEXWb5krd8IyAYsLaLwUTjvATB0FvLgv0";
class Mood extends Component {
  state = {
    user: { ...this.props.user },
    formShow: true
  };
  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };
  toggleForm = () => {
    this.setState({
      formShow: true
    });
  };
  handleSubmit = e => {
    e.preventDefault();

    // pd.emotion(
    //  this.state.text,
    //   "en"
    // )
    //   .then(response => {

    //     this.setState({
    //       formShow: false,
    //       mood: response.emotion
    //     });
    //   })
    //   .catch(error => {
    //     console.log(error);
    //   });
    this.setState({
      formShow: false,
      mood: {
        Happy: 0.0286004865,
        Angry: 0.0304055973,
        Bored: 0.0060389347,
        Fear: 0.1790783869,
        Sad: 0.7433762018,
        Excited: 0.0125003927
      }
    });
  };
  render() {
    {
      console.log(this.state.mood);
    }
    return (
      <div>
        <Navbar />
        <div>
          {this.state.formShow ? (
            <form id="form6" onSubmit={this.handleSubmit}>
              <textarea
                type="text"
                name="text"
                required
                onChange={this.handleChange}
              />
              <div className="form-group">
                <input
                  type="submit"
                  value="Create"
                  className="btn btn-primary"
                />
              </div>
            </form>
          ) : (
            <div>
              <p>This story has been added to your diary</p>
              <div>
                <div>
                  <div>&#128512;</div>
                  <p>Happy</p>
                  <p>{(Number(this.state.mood.Happy) * 100).toFixed(2)}%</p>
                </div>
              </div>
              <button onClick={this.toggleForm}>New thoughts</button>
            </div>
          )}
        </div>
      </div>
    );
  }
}

export default Mood;
